(function () {
    var old = console.log;
    var logger = document.getElementById('log');
    console.log = function (message) {
        if (typeof message == 'object') {
            logger.innerHTML += (JSON && JSON.stringify ? JSON.stringify(message) : message) + '<br />';
            // document.getElementById("log").scrollIntoView({block: "end", inline: "end"});
            var valueScrollY = logger.scrollHeight - logger.clientHeight + 2;
            document.getElementById("log").scroll(0, valueScrollY);
        } else {
            // var Splits = message.split('\n');
            // Splits.forEach(item => logger.innerHTML += item.replace('\n' , '<br />') + '<br />')
            // logger.innerHTML += message.replace
            if (message.includes('\n')) {
                logger.innerHTML += `<p style='color: red'> ${message.replace('\n' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else if (message.includes('red:')) {
                logger.innerHTML += `<p style='color: red'> ${message.replace('red:' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else if (message.includes('blue:')) {
                logger.innerHTML += `<p style='color: #6960EC'> ${message.replace('blue:' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else if (message.includes('bckGreen:')) {
                logger.innerHTML += `<p style='background-color: darkgreen;'> ${message.replace('bckGreen:' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else if (message.includes('yellow:')) {
                logger.innerHTML += `<p style='color: #FDD017;'> ${message.replace('yellow:' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else if (message.includes('grey:')) {
                logger.innerHTML += `<p style='color: grey;'> ${message.replace('grey:' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else if (message.includes('bckRed:')) {
                logger.innerHTML += `<p style='background-color: red;'> ${message.replace('bckRed:' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else if (message.includes('info:')) {
                logger.innerHTML += `<p style='color: #CECECE;'> ${message.replace('info:' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else if (message.includes('aqua:')) {
                logger.innerHTML += `<p style='color: #9AFEFF;'> ${message.replace('aqua:' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            else {
                logger.innerHTML += `<p style='color: white'> ${message.replace('\n' , '')} </p>`
                // logger.innerHTML += message.replace('\n' , "<br />") + '<br />'; 
                var valueScrollY = logger.scrollHeight - logger.clientHeight  + 2;
                document.getElementById("log").scroll(0, valueScrollY);
            }
            
        }
    }
    })();